import * as types from "../constants/ActionTypes";

/**
 * Sends a signal to the reducer to login with the given user
 *
 * @param {obj} user User data from the firebase auth obj
 *
 * @returns reducer action obj with type: LOGIN and user obj
 */
export function login(user) {
    return { type: types.LOGIN, user };
}

/**
 * Sends a signal to the reducer to logout the current user
 *
 * @returns reducer action obj with type: LOGOUT
 */
export function logout() {
    return { type: types.LOGOUT };
}

export function refreshToken(token) {
    return { type: types.REFRESH_TOKEN, token };
}

export default {
    login,
    logout,
    refreshToken
};

import * as types from "../constants/ActionTypes";

export const collectRef = "/apiv1/collections";

export function asyncCollections(id) {
    // fetch user's collections
    return (dispatch) => {
        if (id) {
            let userCollections = [];
            fetch(collectRef, {headers: {"x-access-token": id}}).then((data) => {
                data.json().then((data) => {
                    data.forEach((doc) => {
                        userCollections.push(doc);
                    });
                });
                dispatch(syncCollections(userCollections));
            });
        }
    };
}

export function syncCollections(payload) {
    return { type: types.SYNC_CLASSES, payload: payload };
}

export function asyncCollection(collectionID, uid) {
    // fetch projects in collection
    return (dispatch) => {
        if (collectionID) {
            let collectionProjects = [];
            let projectOptions = [];
            fetch(`${collectRef}/collectionID/${collectionID}`, {headers: {"x-access-token": uid}})
                .then((resp) => {
                    switch(resp.status){
                        case 200:
                            document.title = collectionID + " Collection | MYR";
                            resp.json().then((data) => {
                                data.forEach((doc) => {
                                    collectionProjects.push(doc);
                                });
                                collectionProjects.map((proj) => {
                                    return projectOptions.push({
                                        value: proj._id,
                                        label: proj.name
                                    });
                                });
        
                                dispatch(syncCollection(projectOptions));
                            });
                            break;
                        case 401:
                            window.alert("Error: You are not logged in as the owner of this collection");
                            break;
                        case 404:
                            window.location.assign("/error-404");
                            break;
                        default:
                            window.alert(`Error fetching collection scenes: ${resp.statusText}`);
                    }
                });
        }
    };
}

export function syncCollection(payload) {
    return { type: types.SYNC_CLASS, payload: payload };
}

export function deleteCollection(id, name = null, uid) {
    return (dispatch) => {
        name = (name ? name : id);
        if (window.confirm(`Are you sure you want to delete collection "${name}"?`)) {

            // Delete Document
            fetch(`${collectRef}/collectionID/${name}`, {method: "DELETE", headers: { "x-access-token": uid}}).then((resp) => {
                if(resp.status !== 204) {
                    console.error(`Error deleting collection ${name}: ${resp.statusText}`);
                    return;
                }
                dispatch({ type: types.DELETE_CLASS, id: id });
            });
        }
    };
}
/**
 * 
 * @param {string} name The name of the collection to be created
 * @param {*} uid A JWT token to authenticate with the backend
 */
export async function createCollection(name, uid) {
    name = name.toLowerCase().trim();
    
    let resp = await fetch(`${collectRef}/`, {
        method: "POST", 
        body: JSON.stringify({collectID: name}),
        headers:{"Content-Type": "application/json", "x-access-token": uid}
    });
    if(resp.status === 409){
        window.alert("Error: A collection already exists with that collection name.");
        return false;
    }else if (resp.status !== 201) {
        window.alert(`Error creating collection: ${resp.statusText}`);
        return false;
    }else{
        asyncCollections(uid);
        window.alert("Collection added!");
        return true;
    }
}


export default {
    asyncCollection,
    asyncCollections,
    deleteCollection,
    syncCollection,
    syncCollections,
    createCollection,
    collectRef
};

import { render, updateSavedText  } from "./editorActions";

import * as types from "../constants/ActionTypes";

import * as sceneActions from "./sceneActions";

const courseRef = "/apiv1/courses/";
const header = { headers: { "content-type": "application/json" } };
let noLessons = {
    name: "",
    id: -1,
    prompt: "There are no lessons in this course",
    code: ""
};
const problem = {
    name: "Error",
    id: -1,
    prompt: "There has been an error. Please try reloading the page.",
    code: ""
};

/**
 * Course Actions
 */
export function fetchCourses() {
    return (dispatch) => {
        fetch(courseRef, header)
            .then(response => {
                response.json()
                    .then(json => { dispatch(syncCourses(json)); })
                    // likely a parsing issue
                    .catch(err => {
                        dispatch(loadLesson(problem));
                        console.error(err);
                    });
            })
            // likely an HTTP error
            .catch(err => {
                dispatch(loadLesson(problem));
                console.error(err);
            });
    };
}

export function syncCourses(payload) {
    return { type: types.SYNC_COURSES, payload: payload };
}

export function fetchCourse(courseId) {
    return (dispatch) => {
        fetch(courseRef + courseId, header)
            .then(response => {
                response.json()
                    .then(json => {
                        document.title = json.name + " Course | MYR";
                        dispatch(loadCourse(json));

                        //Make sure that the course is not empty
                        if(json.lessons.length <= 0){
                            noLessons.name = json.name; 
                            dispatch(loadLesson(noLessons));
                            return;
                        }

                        dispatch(loadLesson(json.lessons[0] || ""));
                        dispatch(render(json.lessons[0].code || ""));
                        dispatch(updateSavedText(json.lessons[0].code || ""));
                        dispatch(sceneActions.setNameDesc(
                            {
                                name: json.lessons[0].name,
                                desc: "This scene was saved from the course: " + json.name
                            }));   
                    })
                    .catch(err => {
                        console.error(err);
                        dispatch(loadLesson(problem));
                    });
            })
            .catch(err => {
                console.error(err);
                dispatch(loadLesson(problem));
            });
    };
}

export function loadCourse(course) {
    return {
        type: types.LOAD_COURSE,
        payload: course
    };
}

/**
 * Lesson Actions
 */
export function fetchLesson(json) {
    return (dispatch) => {
        dispatch(loadLesson(json));
        dispatch(render(json.code || ""));
        dispatch(updateSavedText(json.code || ""));
        dispatch(sceneActions.nameScene(json.name));
    };
}

/**
 * Frontend disables option if out of bounds
 * 
 * @param {*} currentIndex !!!DESCRIPTION NEEDED!!!
 * @param {*} next !!!DESCRIPTION NEEDED!!!
 */
export function nextLesson(currentIndex, next) {
    return (dispatch) => {
        dispatch(setCurrentIndex(currentIndex + 1));
        dispatch(fetchLesson(next));
    };
}

/**
 * Frontend disables option if out of bounds
 * 
 * @param {*} currentIndex !!!DESCRIPTION NEEDED!!!
 * @param {*} prev !!!DESCRIPTION NEEDED!!!
 */
export function previousLesson(currentIndex, prev) {
    return (dispatch) => {
        dispatch(setCurrentIndex(currentIndex - 1));
        dispatch(fetchLesson(prev));
    };
}

export function setCurrentIndex(newIndex) {
    return {
        type: types.SET_INDEX,
        payload: newIndex
    };
}

export function loadLesson(lesson) {
    return {
        type: types.LOAD_LESSON,
        payload: lesson
    };
}

export default {
    fetchCourses,
    syncCourses,
    nextLesson,
    previousLesson,
    fetchCourse,
    setCurrentIndex,
    loadLesson,
    fetchLesson,
    loadCourse
};

import { loadScene } from "./sceneActions";
import { DEF_SETTINGS } from "../reducers/scene";
import * as types from "../constants/ActionTypes";

const sceneRef = "/apiv1/scenes";

/**
 * Sends a signal to the reducer to render the scene
 *
 * @param {string} text Text from the Ace Editor component
 *
 * @returns reducer action obj with action type and text
 */
export function render(text, uid) {
    return { type: types.EDITOR_RENDER, text, uid };
}

/**
 * Sends a signal to the reducer to refresh with the given text
 *
 * @param {string} text Text from the Ace Editor component
 *
 * @returns reducer action obj with action type and text
 */
export function refresh(text, uid) {
    return { type: types.EDITOR_REFRESH, text, uid };
}

/**
 * Sends a signal to the reducer to 'rewind' until last stable render
 *
 * @returns reducer action obj with action type
 */
export function recover() {
    return { type: types.EDITOR_RECOVER };
}

/**
 * This does an async fetch to Firebase to grab the scene, then
 * dispatches the necessary functions to update the state.
 */
export function fetchScene(id, uid = "anon") {
    return (dispatch) => {  // Return a func that dispatches events after async
        fetch(`${sceneRef}/id/${id}`, {redirect: "follow"}).then((response) =>{
            if(response.redirected && id !== "error-404"){
                let url = response.url.split("/");
                window.location.assign(`${window.origin}/scene/${url[url.length - 1]}?redirected=true`);
                return;
            }

            if(response.status !== 200){
                if(response.status === 404){
                    window.location.assign(window.origin + "/error-404");
                }else{
                    console.error("Error retrieving scene. Reason: ", response.statusText);
                }
                return;
            }

            response.json().then((json) =>{
                if(json.code){
                    //don't change the title when fetching scene in collection
                    if(!document.title.includes("Collection")) {
                        document.title = json.name + " | MYR";
                    }
                    dispatch(render(json.code, uid || "anon"));
                    dispatch(updateSavedText(json.code));
                    let settings = DEF_SETTINGS;

                    if(json.settings){
                        settings = {...settings, ...json.settings};
                    }

                    dispatch(loadScene({
                        name: json.name ? json.name : "",
                        id: json.uid === uid ? id : 0,
                        ts: json.updateTime ? json.updateTime : Date.now(),
                        desc: json.desc ? json.desc : "",
                        settings: settings
                    }));
                }
            });
        });
    };
}

/**
 * Sends a signal to the reducer to update the savedText when user try to save or open scene/course
 *
 * @returns reducer action obj with action type
 */

export function updateSavedText(savedText){
    return {type: types.EDITOR_UPDATE_SAVEDTEXT, savedText};
}

export function addPassword(payload) {
    return { type: types.ADD_PW, payload };
}

export default {
    render,
    refresh,
    recover,
    fetchScene,
    addPassword,
    updateSavedText,
};

export { default as AuthActions } from "./authActions";
export { default as EditorActions } from "./editorActions";
export { default as ProjectActions } from "./projectActions";
export { default as SceneActions } from "./sceneActions";
export { default as CourseActions } from "./courseActions";
export { default as CollectionActions } from "./collectionActions";
export { default as ReferenceExampleActions } from "./referenceExampleActions";

import * as types from "../constants/ActionTypes";

const sceneRef = "/apiv1/scenes";
const previewRef = "/apiv1/preview/id";

export function asyncUserProj(id) {
    // fetch user's project
    return (dispatch) => {
        if (id) {
            fetch(`${sceneRef}/`, {headers: {"x-access-token": id}}).then((response) =>{
                if(response.status === 200){
                    response.json().then((json) =>{
                        json.forEach(element => {
                            element.url = `${previewRef}/${element._id}`;
                        });
                        dispatch(syncUserProj(json));
                    });
                }
            });
        }
    };
}

export function syncUserProj(payload) {
    return { type: types.SYNC_USER_PROJ, payload: payload };
}

export const asyncExampleProj = () => {
    // fetch example projects
    return (dispatch) => {
        fetch(`${sceneRef}/example`).then((response) =>{
            if(response.status === 200){
                response.json().then((json) =>{
                    json.forEach(element => {
                        element.url = `${previewRef}/${element._id}`;
                    });
                    dispatch(syncExampleProj(json));
                });
            }
        });
    };
};

export function syncExampleProj(payload) {
    return { type: types.SYNC_EXAMP_PROJ, payload: payload };
}

export function deleteProj(uid, id, name) {
    return (dispatch) => {
        if (window.confirm(`Are you sure you want to delete ${name}?`)) {
            //Delete Scene and Image
            fetch(`${sceneRef}/id/${id}`, {method: "delete", headers: {"x-access-token": uid}}).then(response => {
                if(response.status === 204){
                    // Delete Document
                    dispatch({ type: types.DELETE_PROJ, _id: id });
                    if (window.location.href === `${window.origin}/scene/${id}` || window.location.href === `${window.origin}/scene/${id}/`) {
                        window.location.assign(window.origin);
                    }
                    return true;
                }else{
                    return response.json();
                }
            }).then((body) => {
                if(body !== true){
                    console.error("Error deleting scene");
                    console.error(body);

                    alert(`Error Deleting scene ${name}`);
                }
            });
        }
    };
}

/**
 * Saves a scene to MongoDB
 * 
 * @param {*} uid The id of the logged in user 
 * @param {*} scene JSON data of the scene to be saved
 * @param {*} img JPEG Image file of the Scene
 * @param {*} sceneID sceneId to be updated, if undefined, creates a new scene
 * 
 * @returns {*} The ID of the saved scene
 */
export async function save(uid, scene, img, sceneID=undefined){
    let id = undefined;
    let url = `${sceneRef}`;
    let method = "POST";
    const headers = {
        "Content-Type": "application/json",
        "x-access-token": uid
    };

    if(sceneID !== undefined){
        method = "PUT";
        url = `${sceneRef}/id/${sceneID}`;
    }
    let resp = await fetch(url, {method: method, body: JSON.stringify(scene), headers: headers});
    
    if(resp.status === 401){
        method = "POST";
        resp = await fetch(sceneRef, {method: method, body: JSON.stringify(scene), headers: headers});
    }

    if(resp.status !== 201 && resp.status !== 200){
        console.error("Could not create new Scene, are you sure you're logged in?");
        return false;
    }
    let json = await resp.json();
    id = json._id;

    if(id === ""){
        console.error("Error receiving scene id from server");
        return false;
    }

    let data = {data: img};
    await fetch(`${previewRef}/${id}`, {method: method, body: JSON.stringify(data), 
        headers: {"Content-Type": "application/json", "x-access-token": uid}})
        .then((resp) => {
            if(resp.status !== 201 && resp.status !== 204){
                console.error("Error sending preview image to server: ", resp.status, resp.statusText);
            }
        });
    return id;
}

export default {
    asyncUserProj,
    syncUserProj,
    asyncExampleProj,
    syncExampleProj,
    deleteProj
};

import { render } from "./editorActions";

import * as types from "../constants/ActionTypes";

import * as sceneActions from "./sceneActions";

const refExRef = "/apiv1/referenceExamples/";
const header = { headers: { "content-type": "application/json" } };
const problem = {
    functionName: "Error",
    functionParams: [],
    type: "Error",
    info: "An unknown error occurred. Please try refreshing the page.",
    suggestedCourse: null,
    code: ""
};

const notFound = {
    functionName: "Not Found",
    functionParams: [],
    type: "Error 404",
    info: "The function example you are trying to view is not currently defined.",
    suggestedCourse: null,
    code: ""
};

/**
 * Lesson Actions
 * 
 * @param {*} funcName !!!DESCRIPTION NEEDED!!!
 */
export function fetchReferenceExample(funcName) {
    return (dispatch) => {
        fetch(refExRef + funcName, header)
            .then(response => {
                response.json()
                    .then(json => {
                        dispatch(loadReferenceExample(response.status === 200 ? json : notFound));
                        if(response.status === 200) {
                            document.title = json.functionName + " Reference | MYR";
                        } else {
                            document.title = "Reference | MYR";
                        }
                        dispatch(render(json.code || ""));
                        dispatch(sceneActions.setNameDesc(
                            {
                                name: json.functionName,
                                desc: "This scene was saved from the reference example: " + json.functionName,
                            }));
                    })
                    .catch(err => {
                        dispatch(loadReferenceExample(problem));
                        console.error(err);
                    });
            })
            .catch(err => {
                dispatch(loadReferenceExample(problem));
                console.error(err);
            });
    };
}

export function loadReferenceExample(refEx) {
    return {
        type: types.LOAD_REF_EX,
        payload: refEx
    };
}

export default {
    loadReferenceExample,
    fetchReferenceExample
};

import * as types from "../constants/ActionTypes";

/**
 * This function registers the scene"s name with Redux
 *
 * @param {string} name the name is given by the user or when a scene is loaded
 *
 * @returns a reducer action with type:NAME_SCENE
 */
export function nameScene(name) {
    return { type: types.NAME_SCENE, name };
}

/**
 * This function registers the scene's id with Redux
 *
 * @param {string} id the id of the loaded scene
 *
 * @returns a reducer action with type:LOAD_SCENE
 */
export function loadScene(data) {
    return { type: types.LOAD_SCENE, data };
}

export function toggleCoordSky() {
    return { type: types.TOGGLE_COORD_SKY };
}

export function changeSkyColor(color) {
    return { type: types.CHANGE_SKY_COLOR, color };
}

export function changeFloorColor(color) {
    return { type: types.CHANGE_FLOOR_COLOR, color };
}

export function changeCamMode(config) {
    return { type: types.CHANGE_CAM_MODE, config };
}

export function setCamera(x, y, z) {
    return { type: types.SET_CAMERA, x, y, z };
}

export function changePerspective() {
    return { type: types.CHANGE_PERSPECTIVE };
}

export function changeView() {
    return { type: types.CHANGE_VIEW };
}

export function toggleFly() {
    return { type: types.TOGGLE_FLY };
}

export function toggleFloor() {
    return { type: types.TOGGLE_FLOOR };
}

export function loadSettings(payload) {
    return { type: types.LOAD_SETTINGS, payload };
}

export function changeSetting(payload) {
    return { type: types.LOAD_SETTINGS, payload };
}

export function addCollectionID(payload) {
    return { type: types.ADD_CLASSROOM, payload };
}

export function removeCollectionID(payload) {
    return { type: types.REMOVE_CLASSROOM, payload};
}

export function setDesc(payload) {
    return { type: types.SET_DESC, payload };
}

export function setNameDesc(payload) {
    return { type: types.SET_NAME_DESC, payload };
}

export default {
    nameScene,
    loadScene,
    toggleCoordSky,
    changeSkyColor,
    changeFloorColor,
    changeCamMode,
    setCamera,
    changePerspective,
    changeView,
    toggleFly,
    toggleFloor,
    loadSettings,
    changeSetting,
    addCollectionID,
    setDesc,
    setNameDesc,
    removeCollectionID
};

import React, { Component } from "react";
import Select from "react-select";

import {
    Button,
    ButtonBase,
    Icon,
    Modal,
    TextField
} from "@material-ui/core";

import { withStyles } from "@material-ui/core/styles";

import "../../css/Collection.css";

/**
 * FUNC to position modal in the middle of the screen
 */
function getModalStyle() {
    const top = 50;
    const left = 50;
    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
        maxWidth: "90%"
    };
}

/**
 * CSS for modal
 * 
 * @param {*} theme !!!DESCRIPTION NEEDED!!!
 */
const modelStyles = theme => ({
    paper: {
        position: "absolute",
        width: theme.spacing.unit * 60,
        backgroundColor: theme.palette.background.paper,
        boxShadow: theme.shadows[5],
        padding: theme.spacing.unit * 4,
    },
    button: {
        margin: theme.spacing.unit,
    }
});

const btnStyle = {
    base: {
        marginTop: 20,
        justifyContent: "left",
        width: "100%"
    },
    on: {
        color: "#3f51b5",
    },
    off: {
        color: "#333",
    },
    save: {
        padding: 5,
        margin: 5,
        color: "#333",
        width: "100%"
    },
    cancel: {
        padding: 5,
        margin: 5,
        color: "red",
        width: "100%"
    }
};

class CollectionModal extends Component {
    constructor(props) {
        super(props);
        this.state = {
            addOpen: false,
            openOpen: false,
            deleteOpen: false,
            newcollectionID: ""
        };
    }

    handleChange = (selectedCollection) => {
        window.location.assign(window.origin + "/collection/" + selectedCollection.value);
    }

    handleDelete = (selectedCollection) => {
        let needsToRedirect = (this.props.openCollection === selectedCollection.label);
        this.props.collectionActions.deleteCollection(selectedCollection.value, selectedCollection.label, this.props.user.uid);
        this.handleCloseAll();
        if(needsToRedirect){
            window.location.assign("/");
        }
        this.props.deleteCallback(selectedCollection.label);
    }

    handleTextChange = name => event => {
        this.setState({
            [name]: event.target.value,
        });
    };

    handleAddClassToggle = () => {
        this.setState({ addOpen: !this.state.addOpen });
    }

    handleOpenClassToggle = () => {
        this.setState({ openOpen: !this.state.openOpen });
    }

    handleDeleteClassToggle = () => {
        this.setState({ deleteOpen: !this.state.deleteOpen });
    }

    handleCloseAll = () => {
        this.setState({ addOpen: false, openOpen: false, deleteOpen: false });
        this.props.handleCollectionClose();
    }

    selectCollection = () => {
        const userCollections = this.props.collections.collections;
        let optionItems = [];
        const placeholder = "Select a collection";

        userCollections.map((collection) =>
            optionItems.push({
                value: collection.collectionID,
                label: collection.collectionID
            })
        );

        return (
            <div>
                <h5>Select a collection to open.</h5>
                <Select placeholder={placeholder} options={optionItems} onChange={this.handleChange} />
            </div>
        );
    }

    deleteCollection = () => {
        const userCollections = this.props.collections.collections;
        let optionItems = [];
        const placeholder = "Select a collection";

        userCollections.map((collection) =>
            optionItems.push({
                value: collection._id,
                label: collection.collectionID
            })
        );

        return (
            <div>
                <h5>Select a collection to delete.</h5>
                <Select placeholder={placeholder} options={optionItems} onChange={this.handleDelete} />
            </div>
        );
    }

    handleSubmit = () => {
        if (!this.props.user) {
            window.alert("You must be signed in to create a collection.");
            this.handleAddClassToggle();
        }
        else {
            let name = this.state.newcollectionID.toLowerCase();
    
            fetch("/apiv1/collections", {
                method: "POST", 
                body: JSON.stringify({collectID: name}),
                headers:{"Content-Type": "application/json", "x-access-token": this.props.user.uid}
            }).then((resp) => {

                switch(resp.status) {
                    case 201://Success
                        this.props.collectionActions.asyncCollections(this.props.user.uid);
                        window.alert("Collection added!");
                        this.handleCloseAll();
                        break;
                    case 409:
                        window.alert("Error: A collection already exists with that collection name.");
                        break;
                    default:
                        window.alert(`Error creating collection: ${resp.statusText}`);
                        break;
                }
            });
        }
    }

    addClass = () => (
        <div>
            <h5>Please enter a new collection name.</h5>
            <TextField
                id="standard-name"
                type="text"
                onChange={this.handleTextChange("newcollectionID")}
            />
            <Button
                color="primary"
                onClick={() => {
                    this.handleSubmit();
                }} >
                Submit
            </Button>
        </div>
    );

    /**
     * Render all of the elements
     */
    render() {
        const { classes } = this.props;
        return (
            <div>
                <Modal
                    aria-labelledby="simple-modal-title"
                    aria-describedby="simple-modal-description"
                    open={this.props.open}
                    onClose={this.props.handleCollectionToggle} >
                    <div style={getModalStyle()} className={classes.paper}>
                        <ButtonBase
                            style={{ position: "absolute", right: 15, top: 15 }}
                            onClick={this.props.handleCollectionToggle} >
                            <Icon className="material-icons">clear</Icon>
                        </ButtonBase >
                        <div className="row d-flex">
                            <div className="col-12 border-bottom">Collection Options</div>
                            <div className="col-6">
                                <ButtonBase
                                    style={btnStyle.base}
                                    onClick={() => { this.handleOpenClassToggle(); }} >
                                    <Icon className="material-icons collection-icon">storage</Icon>
                                    Open a Collection
                                </ButtonBase>
                                <ButtonBase
                                    style={btnStyle.base}
