            }))});</span>
        );
    };

    TableEx = (category) => {

        return (
            <Table  >
                <TableHead >
                    <TableRow>
                        <TableCell>Name</TableCell>
                        <TableCell>Description</TableCell>
                        <TableCell className='refExample'>Example</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody  >
                    {this.tableData[category].map((row, index) => (
                        <TableRow key={index}>
                            <TableCell >{this.nameHelper(row.name, row.parameters)}</TableCell>
                            <TableCell >{row.description}</TableCell>
                            <TableCell >{this.exampleHelper(row.example)}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        );
    };

    render() {
        document.title = "Reference | MYR";
        return (
            <div id="reference-page">
                <Tabs
                    id="reference-tabs"
                    fullWidth={true}
                    value={this.state.value}
                    onChange={this.handleChange} >
                    <Tab
                        icon={<Icon className="material-icons geometry">category</Icon>}
                        label={
                            <Hidden xsDown>
                                <div>GEOMETRY</div>
                            </Hidden>
                        }
                        value='a' />
                    <Tab
                        icon={<Icon className="material-icons color-change">bubble_chart</Icon>}
                        label={
                            <Hidden xsDown>
                                <div>TRANSFORMATIONS</div>
                            </Hidden>
                        }
                        value='b' />
                    <Tab
                        icon={<Icon className="material-icons animation-ref">zoom_out_map</Icon>} //swap_horiz control_camera category
                        label={
                            <Hidden xsDown>
                                <div>ANIMATIONS</div>
                            </Hidden>
                        }
                        value='c' />
                    <Tab
                        icon={<Icon className="material-icons geometry">widgets</Icon>}
                        label={
                            <Hidden xsDown>
                                <div>GROUPS</div>
                            </Hidden>
                        }
                        value='d' />
                </Tabs>
                {<div style={{ margin: 5 }}>
                    <p style={{ fontSize: "80%" }}> Key: <span className="array">array </span>
                        <span className="bool">bool </span>
                        <span className="number">number </span>
                        <span className="string">string </span>
                        <span className="group">group </span>
                        <span className="data">data</span></p>
                </div>}
                {this.state.value === "a" &&
                    <div style={{ marginTop: 0 }}>
                        {this.TableEx("geometry")}
                    </div>}
                {this.state.value === "b" &&
                    <div style={{ marginTop: 0 }}>
                        {this.TableEx("transformations")}
                    </div>}
                {this.state.value === "c" &&
                    <div style={{ marginTop: 0 }}>
                        {this.TableEx("animations")}
                    </div>}
                {this.state.value === "d" &&
                    <div style={{ marginTop: 0 }}>
                        {this.TableEx("groups")}
                    </div>}
            </div>
        );
    }
}

import React, { Component } from "react";
import {
    Button,
    Icon
} from "@material-ui/core";

class ReferenceExampleBox extends Component {

    suggestedCourse = () => {
        const { suggestedCourse, suggestedCourseName } = this.props.referenceExample;
        return (
            this.props.referenceExample && suggestedCourseName ?
                <Button
                    variant="raised"
                    onClick={() => window.location.assign(`${window.origin}/course/${suggestedCourse}`) }
                    color="primary"
                    className="ref-ex-btn">
                    <Icon className="material-icons">school</Icon>
                    <div id="suggested-course">
                        Suggested Course: {suggestedCourseName}
                    </div>
                </Button>
                : null
        );
    }

    renderParams = (functionParams) => {
        let returnString = "(";
        for (let i = 0; i < functionParams.length; i++) {
            returnString += functionParams[i];
            if (i !== functionParams.length - 1) { returnString += ", "; }
        }
        return returnString += ")";
        // return returnString;
    }

    renderTitle = () => {
        const { functionName, functionParams } = this.props.referenceExample;
        return (
            <h3>{(this.props.referenceExample && functionName) ?
                ("Function: " + functionName + this.renderParams(functionParams))
                : "Loading..."}
            </h3>
        );
    }

    render() {
        return (
            <div id="ref-ex">
                <this.renderTitle />
                <h6>{(this.props.referenceExample && this.props.referenceExample.type) ? "Type: " + this.props.referenceExample.type : "Loading..."}</h6>
                <p>{(this.props.referenceExample && this.props.referenceExample.info) ? this.props.referenceExample.info : "Loading..."} </p>
                <this.suggestedCourse />
            </div>
        );
    }
}


export default ReferenceExampleBox;

import React, { Component, Fragment } from "react";
import Reference from "../../reference/Reference.js";
import Collection from "../../collection/Collection.js";
import SceneConfigMenu from "./SceneConfigMenu.js";
import Sidebar from "./Sidebar.js";
import MyrTour from "./MyrTour.js";
import ProjectView from "./ProjectView.js";
import CourseSelect from "../../courses/CourseSelect.js";
import WelcomeScreen from "../WelcomeScreen.js";
import { GoogleLogin, GoogleLogout } from "react-google-login";
import sockets from "socket.io-client";

import * as layoutTypes from "../../../constants/LayoutTypes.js";

import {
    Button,
    Icon,
    MenuItem,
    Tooltip,
    Drawer,
    IconButton,
    FormControl,
    TextField,
    Snackbar,
    Popover,
    Avatar,
    createMuiTheme,
    MuiThemeProvider
} from "@material-ui/core";
import { save } from "../../../actions/projectActions.js";

const exitBtnStyle = {
    position: "fixed",
    top: 0,
    right: 0,
};

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            logMenuOpen: false,
            availProj: [],
            sampleProj: [],
            collectionOpen: false,
            projectsOpen: false,
            projectTab: "a",
            snackOpen: false,
            lastMsgTime: 0,
            anchorEl: null,
            navAwayModal: false,
            needsNewId: false, // this explicitly tells us to make a new id
            spinnerOpen: false,
            referenceOpen: false,
            coursesOpen: false,
            tourOpen: false,
            welcomeOpen: false,
            updateCollection: false,
            fetchCollection: false,
            socket: sockets(),
            savedSettings: [],
            googleUser: undefined
        };

        this.state.socket.on("update", () => {
            let editor = window.ace.edit("ace-editor");            
            if(editor.getSession().getValue() === this.props.scene.code || window.confirm("A new version of the scene is available, would you like to load it?")){
                this.props.actions.fetchScene(this.props.projectId);
            }
        });
    }

    /**
     * When the component is done rendering, we want to:
     */
    componentDidMount() {
        this.props.projectActions.asyncExampleProj();
        this.props.courseActions.fetchCourses();

        if (this.props.courseName) {
            this.props.courseActions.fetchCourse(this.props.courseName);
        }
        else if (this.props.refExName) {
            this.props.referenceExampleActions.fetchReferenceExample(this.props.refExName);
        }
        else if (this.props.collection) {
            this.setState({ fetchCollection: true });
        }

        // Render project if we have projectId. This should only happen if coming from viewer
        const { match } = this.props;
        const projectId = (match && match.params && match.params.id) || "";
        if (this.props.match && projectId) {
            this.setState({ spinnerOpen: true });
            this.props.actions.fetchScene(projectId);
            this.setState({ spinnerOpen: false });

            this.state.socket.emit("scene", projectId);
        }

        // Bind to keyboard to listen for shortcuts
        document.addEventListener("keydown", this.handleKeyDown.bind(this));
    }

    /**
     * Catches certain keyboard shortcuts
     *
     * @param {event} e - event from the keystroke.
     * 
     * @return {void}
     */
    handleKeyDown(e) {
        //metaKey is cmd and windows key in some browsers
        if (this.props.layoutType !== layoutTypes.REFERENCE) {
            if ((e.ctrlKey || e.metaKey) && (e.key === "Enter" || e.key === "Return")) {
                //ctrl/cmd + enter renders the scene
                e.preventDefault();
                this.clear();
                this.closeSnackBar();
                this.handleRender();
            } else if ((e.ctrlKey || e.metaKey) && e.shiftKey && (e.key === "s" || e.key === "S")) {
                //ctrl/cmd + shift + s saves the scene with a new ID
                e.preventDefault();
                this.setState({ needsNewId: true });
                this.handleSaveOpen();
            } else if ((e.ctrlKey || e.metaKey) && (e.key === "s" || e.key === "S")) {
                //ctrl/cmd + s saves the scene
                e.preventDefault();
                this.handleSave();
                this.handleSaveClose();
            }
        }
    }

    /**
     * Removes listener for real time sync process
     */
    componentWillUnmount() {
    }

    /**
     * When we update, check to see if there is a new message by comparing the local state to
     * props.message.time
     */
    componentDidUpdate() {
        if (this.state.lastMsgTime !== this.props.message.time && this.props.message.text !== "") {
            this.setState({ snackOpen: true, lastMsgTime: this.props.message.time });
        }
        if(this.state.updateCollection && this.props.user){
            this.props.collectionActions.asyncCollection(this.props.collection, this.props.user.uid);
            this.setState({ updateCollection: false });
        }
        if(this.state.fetchCollection && this.props.user){
            this.props.collectionActions.asyncCollection(this.props.collection, this.props.user.uid);
            this.setState({ fetchCollection: false });
        }
        if(this.state.savedSettings.length === 0 && this.props.scene.id !== 0){
            this.setState({savedSettings: this.buildSettingsArr()});

            window.addEventListener("beforeunload", (event) => {
                let finalSettings = this.buildSettingsArr();
    
                if(!this.settingsEqual(finalSettings)){
                    event.preventDefault();
                    event.returnValue = "You may have unsaved changes!";
                }
            });
        }
    }


    buildSettingsArr = () => {
        const sceneSettings = this.props.scene.settings;

        return [sceneSettings.canFly, sceneSettings.floorColor, 
            sceneSettings.showCoordHelper, sceneSettings.showFloor,
            sceneSettings.skyColor, sceneSettings.viewOnly];
    };
    
    settingsEqual = (newSettings) =>{
        for(let i = 0; i < newSettings.length; ++i){
            if(newSettings[i] !== this.state.savedSettings[i]){
                return false;
            }
        }
        return true;
    }

    /**
     * The logout function runs when the user click to logout of the application.
     */
    logout = () => {
        // sync with application state
        this.props.logging.logout();
        this.props.projectActions.syncUserProj([]);
        this.setState({ logMenuOpen: false });
    }

    /**
     * The login function runs when the user click to login of the application.
     */
    login = (googleAuth) => {
        //googleAuth.getAuthResponse().id_token;
        googleAuth.profileObj["uid"] = googleAuth.getAuthResponse().id_token;
        this.props.logging.login(googleAuth.profileObj);
        this.setState({ logMenuOpen: false, googleUser: googleAuth });
        
        this.props.projectActions.asyncUserProj(this.props.user.uid);
        this.props.collectionActions.asyncCollections(this.props.user.uid);
        this.setRefreshTime(googleAuth.tokenObj.expires_at);

        //send uid to google analyrica
        window.gtag("config", "UA-122925714-1", {"user_id": this.props.user.googleId});
    }

    setRefreshTime = (time) => {
        const oneMinute = 60*1000;
        let expiryTime = Math.max(
            oneMinute*5, //Default of 5 minutes
            time - Date.now() - oneMinute*5 // give 5 mins of breathing room
        );
        setTimeout(this.refreshToken, expiryTime);
    }

    refreshToken = () => {
        this.state.googleUser.reloadAuthResponse().then((authResponse) => {
            this.props.logging.refreshToken(authResponse.id_token);
            this.setRefreshTime(authResponse.expires_at);
        });
    }

    /**
     * This function produces the DOM elements to display logging functionality
     */
    loginBtn = () => {
        return (
            <div id="user" >
                {this.props.user && this.props.user.name ?
                    <GoogleLogout
                        clientId={process.env.REACT_APP_GOOGLE_CLIENTID}
                        buttonText="Logout"
                        render={renderProps => (
                            <Fragment>
                                <Avatar
                                    id="login"
                                    src={this.props.user.imageUrl}
                                    open={this.state.logMenuOpen}
                                    onClick={() => this.setState({ logMenuOpen: !this.state.logMenuOpen })}
                                    label="logout"
                                    style={{ marginTop: 5 }} />
                                <Popover
                                    open={this.state.logMenuOpen}
                                    anchorEl={document.getElementById("user")}
                                    anchorOrigin={{ horizontal: "right", vertical: "bottom" }}
                                    onClose={this.handleLogClick} >
                                    <MenuItem primarytext="Log Out" onClick={renderProps.onClick}>Log Out</MenuItem>
                                </Popover>
                            </Fragment>
                        )}
                        onLogoutSuccess={this.logout}
                        onFailure={(err) => console.error("Could not logout: ", err) }
                    />
                    :
                    <GoogleLogin
                        clientId={process.env.REACT_APP_GOOGLE_CLIENTID}
                        buttonText="Login"
                        isSignedIn={true}
                        render={renderProps => (
                            <Button
                                type="button"
                                variant="outlined"
                                size="small"
                                color="primary"
                                onClick={renderProps.onClick}
                                style={{
                                    color: "white",
                                    margin: 4,
                                    padding: 2,
                                    border: "1px solid #fff"
                                }}>
                                    Log In
                            </Button>
                        )}
                        onSuccess={this.login}
                        onFailure={(err) => { console.error("Error logging in: ", err); }}
                    />
                }
            </div>
        );
    }

    /**
     * This function handles when the user wants to toggle the logging menu
     */
    handleLogClick = (event) => {
        event.preventDefault();
        this.setState({
            logMenuOpen: !this.state.logMenuOpen,
        });
    };

    /**
     * This sets the components current state to the input from the scene name form
     */
    handleNameChange = (event) => {
        this.props.sceneActions.nameScene(event.target.value);
    }

    /**
     * This sets the components current state to the input from the scene description form
     */
    handleDescChange = (event) => {
        this.props.sceneActions.setDesc(event.target.value);
    }

    /**
     * This function produces the form for inputting the scene's name and description
     */
    sceneName = () => {
        let sceneName = this.props.scene.name;
        let sceneDesc = this.props.scene.desc;
        return (
            <FormControl className="mt-2" aria-describedby="name-helper-text">
                <TextField id="name-helper"
                    value={sceneName ? sceneName : ""}
                    label="Scene Name"
                    placeholder={sceneName ? sceneName : "Untitled Scene"}
                    onBlur={this.handleNameChange}
                    onChange={this.handleNameChange} />
                <TextField
                    value={sceneDesc ? sceneDesc : ""}
                    onChange={this.handleDescChange}
                    onBlur={this.handleDescChange}
                    label="Description"
                    margin="normal"
                />
            </FormControl>
        );
    }

    /**
     * handeRender gets the information from Ace Editor and calls the action: render()
     */
    handleRender = () => {
        try {
            let editor = window.ace.edit("ace-editor");
            this.props.actions.render(editor.getSession().getValue(), this.props.user ? this.props.user.uid : "anon");
        } catch (error) {
            this.props.actions.render(this.props.text, this.props.user ? this.props.user.uid : "anon");
        }
    }

    /**
     * This function will determine which projectId to use when saving.
     *
     * @returns - projectId
     */
    getProjectId = () => {
        const { match } = this.props;
        let projectId = (match && match.params && match.params.id) || null;
        return projectId;
    }

    spinner = () => {
        if (this.state.spinnerOpen) {
            return (
                <span className='spinner'>
                    <div className='cube1'></div>
                    <div className='cube2'></div>
                </span>
            );
        } else {
            return null;
        }
    }

    /**
     * When the user clicks save it will upload the information to Firebase
     */
    handleSave = (newCollectionID = undefined) => {
        let editor, text;
        if (!this.props.viewOnly) {
            //If in editor mode, gets text directly from editor
            editor = window.ace.edit("ace-editor");
            text = editor.getSession().getValue();
        } else {
            //Otherwise, gets text from state (should be up to date since it is refreshed on editor unmount) 
            text = this.props.text;
        }

        if (this.props.user && this.props.user.uid && text) {
            this.setState({ spinnerOpen: true });
            let scene = document.querySelector("a-scene");
            // Access the scene and screen shot, with perspective view in a lossy jpeg format
            let img = scene.components.screenshot.getCanvas("perspective").toDataURL("image/jpeg", 0.1);

            let newScene = {
                name: (this.props.scene.name ? this.props.scene.name : "Untitled Scene"),
                desc: this.props.scene.desc,
                code: text,
                uid: this.props.user.uid,
                settings: {
                    ...this.props.scene.settings,
                    collectionID: newCollectionID || this.props.scene.settings.collectionID
                },
                updateTime: Date.now(),
                createTime: (this.props.scene.createTime ? this.props.scene.createTime : Date.now())
            };
            
            save(this.props.user.uid, newScene, img, this.props.projectId).then((projectId) =>{
                if(!projectId) {
                    console.error("Could not save the scene");
                }
                
                this.props.actions.updateSavedText(text);
                // If we have a new projectId reload page with it
                if (projectId !== this.props.projectId) {
                    this.setState({ spinnerOpen: false });
                    window.location.assign(`${window.origin}/scene/${projectId}`);
                    this.props.projectActions.asyncUserProj(this.props.user.uid);
                }
                if(!this.state.viewOnly) {
                    this.props.actions.refresh(text, this.props.user ? this.props.user.uid : "anon");
                }
                this.setState({spinnerOpen: false, saveOpen: false});
                this.state.socket.emit("save");
                return true;
            });
        } else if(!text) {
            alert("There is no code to save for this scene. Try adding some in the editor!");
        }else {
            // TODO: Don't use alert
            alert("We were unable to save your project. Are you currently logged in?");
        }

        if(!this.state.viewOnly) {
            this.props.actions.refresh(text, this.props.user ? this.props.user.uid : "anon");
        }
        this.setState({savedSettings: this.buildSettingsArr()});
    }

    /**
     * resets the current scene
     */
    clear = () => {
        try {
            let editor = window.ace.edit("ace-editor");
            this.props.actions.refresh(editor.getSession().getValue(), this.props.user ? this.props.user.uid : "anon");
        } catch (error) {
            this.props.actions.refresh(this.props.text, this.props.user ? this.props.user.uid : "anon");
        }
    }

    /**
     * toggles the save drawer
     */
    handleSaveToggle = () => this.setState({ saveOpen: !this.state.saveOpen });

    /**
     * forces save drawer closed
     */
    handleSaveClose = () => this.setState({ saveOpen: false });

    /**
     * forces save drawer closed
     */
    handleSaveOpen = () => this.setState({ saveOpen: true });

    /**
     * creates the save drawer
     */
    saveDrawer = () => {
        return (
            <Drawer
                variant="persistent"
                className="side-drawer"
                open={this.state.saveOpen}
                onClose={this.handleSaveToggle} >
                <IconButton variant="raised"
                    color="default"
                    style={exitBtnStyle}
                    onClick={this.handleSaveToggle}>
                    <Icon className="material-icons">close</Icon>
                </IconButton>
                <this.sceneName />
                <Button
                    variant="raised"
                    size="small"
                    color="primary"
                    onClick={() => this.handleSave(false)}
                    className="">
                    <Icon className="material-icons">save</Icon> Save
                </Button>
            </Drawer>
        );
    }

    /**
     * toggles the load project drawer
     */
    handleProjectToggle = () => {
        this.setState({ projectsOpen: !this.state.projectsOpen });
        this.setState({ projectTab: "a" });
    };

    handleWelcomeToggle = () => {
        this.setState({ welcomeOpen: !this.state.welcomeOpen });
    };

    handleCoursesToggle = () => {
        this.setState({ coursesOpen: !this.state.coursesOpen });
    };

    handleTourToggle = () => {
        this.setState({ tourOpen: !this.state.tourOpen });
    };

    handleCollectionToggle = () => {
        this.setState({ collectionOpen: !this.state.collectionOpen });
    };

    handleCollectionClose = () => {
        this.setState({ collectionOpen: false });
    };

    handleReferenceToggle = () => {
        this.setState({ referenceOpen: !this.state.referenceOpen });
    };

    handleCollectionDelete = (collectionID) => {
        if(this.props.scene.settings.collectionID === collectionID) {
            this.props.sceneActions.removeCollectionID(this.props.scene);
        }
    }

    loadCollection = () => {
        return (
            <Collection
                openCollection={this.props.collection}
                collections={this.props.collections}
                collectionActions={this.props.collectionActions}
                user={this.props.user}
                open={this.state.collectionOpen}
                handleCollectionToggle={this.handleCollectionToggle}
                handleCollectionClose={this.handleCollectionClose} 
                deleteCallback={this.handleCollectionDelete} />
        );
    }

    /**
     * closes the snackbar that displays the message from render
     */

    closeSnackBar = () => {
        this.setState({ snackOpen: false });
    }

    renderSnackBar = () => {
        return (
            <Snackbar
                anchorOrigin={{
                    vertical: "bottom",
                    horizontal: "left",
                }}
                open={this.state.snackOpen}
                autoHideDuration={6000}
                onClose={this.closeSnackBar}
                ContentProps={{
                    "aria-describedby": "message-id",
                }}
                message={<span id="message-id">{this.props.message.text}</span>}
                action={[
                    <Button key="undo" color="secondary" size="small" onClick={this.closeSnackBar}>
                        Dismiss
                    </Button>
                ]}
            />
        );
    }

    /**
     * creates the header and links the buttons
     */
    render() {
        const style = {
            play: {
                margin: 5,
                padding: 0,
                background: "linear-gradient(45deg, #38e438 30%, #58e458 90%)",
            },
            play_disabled: {
                margin: 5,
                padding: 0,
                background: "#222",
                border: "2px solid",
                borderColor: "#777",
            },
            clear: {
                margin: 5,
                marginRight: 10,
                padding: 0,
                background: "linear-gradient(45deg, #FE3B3B 30%, #FF3B3B 90%)",
            },
            clear_disabled: {
                margin: 5,
                marginRight: 10,
                padding: 0,
                background: "#222",
                border: "2px solid",
                borderColor: "#777",
            },
            default: {
                margin: 2,
                padding: 0,
                color: "#fff",
            },
            disabled: {
                margin: 2,
                padding: 0,
                color: "#777",
            },
        };
        const theme = createMuiTheme({
            palette: {
                primary: {
                    main: "#777",
                }
            }
        });
        const referenceMode = this.props.layoutType === layoutTypes.REFERENCE;
        return (
            <header className="App-header align-items-center ">
                <div className="col-9 d-flex justify-content-start" style={{ paddingLeft: 0 }}>
                    <Sidebar scene={this.props.scene} nameScene={this.props.sceneActions.nameScene} >
                        <Button
                            variant="raised"
                            onClick={() => { window.location.assign(window.origin); }}
                            color="primary"
                            className="sidebar-btn">
                            <Icon className="material-icons">add</Icon>
                            Start New
                        </Button>
                        <Button
                            variant="raised"
                            onClick={this.props.actions.recover}
                            color="primary"
                            className="sidebar-btn"
                            disabled={referenceMode}>
                            <Icon className="material-icons">replay</Icon>
                            Recover
                        </Button>
                        <Button
                            variant="raised"
                            onClick={this.handleSaveToggle}
                            color="primary"
                            className="sidebar-btn"
                            disabled={referenceMode}>
                            <Icon className="material-icons">save</Icon>
                            Save Project
                        </Button>
                        <Button
                            variant="raised"
                            onClick={this.handleProjectToggle}
                            color="primary"
                            className="sidebar-btn">
                            <Icon className="material-icons">perm_media</Icon>
                            Open Project
                        </Button>
                        <Button
                            variant="raised"
                            onClick={this.handleCollectionToggle}
                            color="primary"
                            className="sidebar-btn">
                            <Icon className="material-icons">assignment</Icon>
                            Collections
                        </Button>
                        <Button
                            variant="raised"
                            onClick={this.handleWelcomeToggle}
                            color="primary"
                            className="sidebar-btn">
                            <Icon className="material-icons">wb_iridescent</Icon>
                            Show Welcome Screen
                        </Button>
                    </Sidebar>
                    <h1 className="mr-2 d-none d-sm-block"
                        style={{ cursor: "pointer" }}
                        onClick={() => { window.location.assign(window.origin); }} >
                        MYR
                    </h1>
                    <MuiThemeProvider theme={theme}>
                        <Tooltip title="Render" placement="bottom-start">
                            <Button
                                id="play-btn"
                                variant={referenceMode ? "outlined" : "contained"}
                                size="small"
                                onClick={() => {
                                    this.clear();
                                    this.postpone(this.handleRender);
                                }}
                                color="primary"
                                className="header-btn"
                                style={referenceMode ? style.play_disabled : style.play}
                                disabled={referenceMode}>
                                <Icon className="material-icons" style={referenceMode ? { color: "#777" } : { color: "#222" }}>play_arrow</Icon>
                            </Button>
                        </Tooltip>
                        <WelcomeScreen
                            handleWelcomeToggle={this.handleWelcomeToggle}
                            welcomeOpen={this.state.welcomeOpen}
                            deleteFunc={this.props.projectActions.deleteProj}
                            userProjs={this.props.projects.userProjs}
                            exampleProjs={this.props.projects.exampleProjs}
                            courses={this.props.courses.courses}
                            handleTourToggle={this.handleTourToggle} />
                        <Tooltip title="Stop" placement="bottom-start">
                            <Button
                                id="stop-btn"
                                variant={referenceMode ? "outlined" : "contained"}
                                size="small"
                                onClick={this.clear}
                                color="primary"
                                className="header-btn"
                                style={referenceMode ? style.clear_disabled : style.clear}
                                disabled={referenceMode}>
                                <Icon className="material-icons" style={referenceMode ? { color: "#777" } : { color: "#222" }}>stop</Icon>
                            </Button>
                        </Tooltip>
                    </MuiThemeProvider>
                    <Tooltip title="New Scene" placement="bottom-start">
                        <IconButton
                            id="new-btn"
                            onClick={() => { window.location.assign(window.origin); }}
                            style={style.default}
                            className="header-btn d-none d-md-block" >
                            <Icon className="material-icons">add_circle_outline</Icon>
                        </IconButton>
                    </Tooltip>
                    <Tooltip title="Save" placement="bottom-start">
                        <IconButton
                            id="save-btn"
                            onClick={this.handleSaveToggle}
                            className="header-btn d-none d-sm-block"
                            style={referenceMode ? style.disabled : style.default}
                            disabled={referenceMode}>
                            <Icon className="material-icons">save</Icon>
                        </IconButton>
                    </Tooltip>
                    <ProjectView
                        deleteFunc={this.props.projectActions.deleteProj}
                        userProjs={this.props.projects.userProjs}
                        exampleProjs={this.props.projects.exampleProjs}
                        projectsOpen={this.state.projectsOpen}
                        handleProjectToggle={this.handleProjectToggle}
                        tab={this.state.projectTab}
                        user={this.props.user} />
                    <MyrTour
                        tourOpen={this.state.tourOpen}
                        handleTourToggle={this.handleTourToggle}
                        viewOnly={this.props.scene.settings.viewOnly}
                        changeView={this.props.sceneActions.changeView}
                        layoutType={this.props.layoutType}
                        referenceOpen={this.state.referenceOpen}
                        handleReferenceToggle={this.handleReferenceToggle} />
                </div>
                <div className="col-3 d-flex justify-content-end">
                    <Reference
                        layoutType={this.props.layoutType}
                        referenceOpen={this.state.referenceOpen}
                        handleReferenceToggle={this.handleReferenceToggle} />
                    <SceneConfigMenu
                        scene={this.props.scene}
                        sceneActions={this.props.sceneActions}
                        collectionActions={this.props.collectionActions}
                        user={this.props.user}
                        handleRender={this.handleRender}
                        handleSave={this.handleSave}
                        handleSaveClose={this.handleSaveClose}
                        layoutType={this.props.layoutType}
                        displayCollectionConfig={!this.props.collection}
                    />
                    <CourseSelect
                        coursesOpen={this.state.coursesOpen}
                        handleCoursesToggle={this.handleCoursesToggle}
                        courses={this.props.courses.courses} />
                    <this.loginBtn />
                </div>
                <this.saveDrawer />
                <this.renderSnackBar />
                <this.spinner />
                <this.loadCollection />
            </header >
        );
    }

    /**
     * You can pass functions into this in order to have
     * multiple setState/state actions dispatched within an event handler
     * Currently only used for render button
     * 
     * @param {*} f 
     */
    postpone(f) {
        window.setTimeout(f, 0);
    }
}

export default Header;

import React, { Component } from "react";
import Tour from "reactour";
import { Button } from "@material-ui/core";
import * as layoutTypes from "../../../constants/LayoutTypes";

class MyrTour extends Component {
    constructor(props) {
        super(props);
        this.state = {
            viewOnlyOnOpen: this.props.viewOnly
        };
    }

    closeTour = () => {
        if (this.state.viewOnlyOnOpen) {
            this.props.changeView();
        }
        this.props.handleTourToggle();
    }

    render() {
        let isDisabled = this.props.layoutType === layoutTypes.REFERENCE;
        return (
            <React.Fragment>
                {!isDisabled ?
                    <React.Fragment>
                        <Tour
                            steps={steps}
                            isOpen={this.props.tourOpen}
                            onAfterOpen={() => {
                                this.setState({ viewOnlyOnOpen: this.props.viewOnly });
                                if (this.props.viewOnly) {
                                    this.props.changeView();
                                }
                            }}
                            onRequestClose={this.closeTour} />
                        <Button
                            style={{ color: "#fff", fontSize: "66%" }}
                            size="small"
                            className="d-none d-md-block"
                            onClick={() => {
                                this.props.handleTourToggle();
                                if (this.props.referenceOpen) {
                                    this.props.handleReferenceToggle();
                                }
                            }}>
                            Take the Tour
                        </ Button>
                    </React.Fragment>
                    : null
                }
            </React.Fragment>
        );
    }
}

const steps = [
    {
        selector: "#ace-editor",
        content: "This is the editor. You can create 3D scenes using JavaScript " +
            "and a special set of instructions or functions to MYR.\n The editor can be " +
            "toggled on and off by opening the settings menu in the top right and " +
            "clicking the \"View Editor\" switch."
    },
    {
        selector: "#play-btn",
        content: "The Play button will render the scene."
    },
    {
        selector: "#stop-btn",
        content: "The Stop button will stop the scene. \n Use this to save battery."
    },
    {
        selector: "#scene",
        content: "The View is where you can see your work. \n Click the goggle to view in VR."
    },
    {
        selector: "#new-btn",
        content: "Create a new scene from scratch. Be sure to save first!",
    },
    {
        selector: "#save-btn",
        content: "Save your work.",
    },
    {
        selector: "#open-btn",
        content: "See previous work and view examples.",
    },
    {
        selector: "#ref-btn",
        content: "Use the Reference to see all MYR has to offer.",
    },
    {
        selector: "#configure-scene",
        content: "Modify and share your scene including setting the background color, enabling the grid, and enabling flying.",
    },
    {
        selector: "#select-course",
        content: "View the list of available courses to get you started on using MYR.",
    },
    {
        selector: "#user",
        content: "You can log in with a Google account in order to save your scenes.",
    },
];

export default MyrTour;

import React from "react";
import QRCode from "qrcode.react";
import {
    Button,
    ButtonBase,
    Icon,
    IconButton,
    ListItemIcon,
    ListItemText,
    Menu,
    MenuItem,
    Modal,
    TextField,
    Tooltip,
    Tabs,
    Tab
} from "@material-ui/core";

import { withStyles } from "@material-ui/core/styles";
import "../../../css/ProjectView.css";

function getOuterModalStyle() {
    const top = 50;
    const left = 50;

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
        maxWidth: "90%",
        maxHeight: "90%"
    };
}

function getModalStyle() {
    const top = 50;
    const left = 50;

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

function getInfoModalStyle() {
    const top = 50;
    const left = 50;

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

// CSS for modal
const modelStyles = theme => ({
    outer: {
        position: "absolute",
        width: theme.spacing.unit * 150,
        backgroundColor: theme.palette.background.paper,
        boxShadow: theme.shadows[5],
        padding: theme.spacing.unit * 4,
        overflow: "scroll"
    },
    paper: {
        position: "absolute",
        width: theme.spacing.unit * 50,
        backgroundColor: theme.palette.background.paper,
        boxShadow: theme.shadows[5],
        padding: theme.spacing.unit * 4,
    },
    info: {
        position: "absolute",
        width: theme.spacing.unit * 100,
        backgroundColor: theme.palette.background.paper,
        boxShadow: theme.shadows[5],
        padding: theme.spacing.unit * 4,
    },
    button: {
        margin: theme.spacing.unit,
    }
});

const exitBtnStyle = {
    position: "fixed",
    top: 0,
    right: 0,
};
class Project extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            deleteFunc: this.props.deleteFunc,
            showImg: false,
            anchorEl: null,
            qrCodeOpen: false,
            pwProtectOpen: false,
            shareOpen: false,
            infoOpen: false,
            email: "",
            pw: "",
            sendTo: [],
            value: this.props.tab,
        };
        this.emailRef = React.createRef();
    }

    handleClick = event => {
        this.setState({ anchorEl: event.currentTarget, projectId: event.currentTarget.id });
    };

    handleInfoUserClick = event => {
        this.setState({ projectId: event.currentTarget.id, isUserProj: true });
        this.handleInfoToggle();
    };

    handleInfoExampleClick = event => {
        this.setState({ projectId: event.currentTarget.id, isUserProj: false });
        this.handleInfoToggle();
    };

    handleClose = () => {
        this.setState({ anchorEl: null });
    };

    handleTextChange = name => event => {
        this.setState({
            [name]: event.target.value,
        });
    };

    handleAddEmail = () => {
        let arr = [].concat(this.state.sendTo);
        arr.push(this.state.email);
        this.emailRef.current.value = "";
        this.setState({ sendTo: arr, email: "" });
    }

    handleInfoToggle = () => {
        this.setState({ infoOpen: !this.state.infoOpen });
    }

    handleQrToggle = () => {
        this.setState({ qrCodeOpen: !this.state.qrCodeOpen });
    }

    handlePwToggle = () => {
        this.setState({ pwProtectOpen: !this.state.pwProtectOpen });
    }

    handleShrToggle = () => {
        this.setState({ shareOpen: !this.state.shareOpen, sendTo: [] });
    }
    //handleProjectToggle = () => {
    //  this.setState({ projectsOpen: !this.state.projectsOpen });
    //  this.setState({ value: "a" });
    //};

    // pwProtect = () => (
    //   <div>
    //     <h5>Please enter a PW.</h5>
    //     <TextField
    //       type="password"
    //       label="Password"
    //       value={this.state.pw}
    //       onChange={this.handleTextChange("pw")}
    //       margin="normal"
    //     />
    //     <Button
    //       color="primary"
    //       onClick={this.handlePwToggle} >
    //       Save
    //     </Button>
    //     <p style={{ fontSize: "80%", marginTop: 10 }}>
    //       <b>Legal disclaimer:</b> This will only slow down people from accessing your work. MYR is not sutiable for sensitive information.
    //     </p>
    //   </div>
    // );

    // pwProtect = () => (
    //   <div>
    //     <h5>Please enter a PW.</h5>
    //     <TextField
    //       type="password"
    //       label="Password"
    //       value={this.state.pw}
    //       onChange={this.handleTextChange("pw")}
    //       margin="normal"
    //     />
    //     <Button
    //       color="primary"
    //       onClick={this.handlePwToggle} >
    //       Save
    //     </Button>
    //     <p style={{ fontSize: "80%", marginTop: 10 }}>
    //       <b>Legal disclaimer:</b> This will only slow down people from accessing your work. MYR is not sutiable for sensitive information.
    //     </p>
    //   </div>
    // );

    shareOptions = () => (
        <div>
            <h5>Enter one or more email addresses</h5>
            {
                this.state.sendTo.map(x => {
                    return <p>{x}</p>;
                })
            }
            <TextField
                id="standard-name"
                label="Email"
                value={this.state.email}
                inputRef={this.emailRef}
                onChange={this.handleTextChange("email")}
                margin="normal"
            />
            <IconButton
                variant="raised"
                onClick={this.handleAddEmail}
                color="primary">
                <Icon className="material-icons">add</Icon>
            </IconButton>
            <Button
                color="primary"
                onClick={this.handleAddEmail}
                href={`mailto:${this.state.sendTo.join("; ")}?subject=Check out my VR Scene in MYR&body=You can find my scene at ${window.origin + "/scene/" + this.state.projectId}`}>
                Send
            </Button>
        </div>
    );

    infoOpen = () => {
        let projectId = this.state.projectId;
        let project;
        if (this.state.isUserProj) {
            project = this.props.userProjs.find(function (project) {
                return project._id === projectId;
            });
        }
        else {
            project = this.props.exampleProjs.find(function (project) {
                return project._id === projectId;
            });
        }
        if (!project) {
            return (
                <div>
                    <h5>Project Information</h5>
                    <h6>Error: Unable to load project information</h6>
                </div>
            );
        }
        let lastMod = new Date(project.updateTime);
        return (
            <div>
                <h3>{project.name}</h3>
                <p id="info-description">{project.desc}</p>
                <small>Last Modified: {lastMod.toDateString()}</small>
            </div>
        );
    };

    qrCodeOpen = () => {
        return (
            <div>
                <h5>QR Code to Your Project</h5>
                <QRCode size={330} value={window.origin + "/scene/" + this.state.projectId} />
            </div>
        );
    };

    helper = (proj, canDelete) => {
        if (proj) {
            let id = proj._id;
            let name = proj.name;
            return (
                <div key={id} id={id} title={name}
                    className="proj col-xs-12 col-md-6 col-lg-4 pt-2 pl-0" >
                    <a href={`/scene/${id}`} >
                        <span className="project-span">{name}</span>
                        <img id={id} alt={id} src={proj.url}
                            className={"img-thumbnail " + (this.state.showImg && "d-none")} />
                    </a>
                    {canDelete ?
                        <span className="scene-btns">
                            <IconButton
                                id={id}
                                color="primary"
                                onClick={this.handleInfoUserClick}
                                className="" >
                                <Icon className="material-icons">info</Icon>
                            </IconButton>
                            <IconButton
                                id={id}
                                color="primary"
                                onClick={this.handleClick}
                                className="" >
                                <Icon className="material-icons">share</Icon>
                            </IconButton>
                            <IconButton
                                label="delete Project"
                                color="secondary"
                                fullwidth={String(!this.state.showImg)}
                                onClick={() => this.props.deleteFunc(this.props.user.uid, id, proj.name)}>
                                <Icon className="material-icons">delete</Icon>
                            </IconButton>
                        </span>
                        : <span className="scene-btns">
                            <IconButton
                                id={id}
                                color="primary"
                                onClick={this.handleInfoExampleClick}
                                className="" >
                                <Icon className="material-icons">info</Icon>
                            </IconButton>
                            <IconButton
                                id={id}
                                color="primary"
                                onClick={this.handleClick}
                                className="" >
                                <Icon className="material-icons">share</Icon>
                            </IconButton>
                        </span>
                    }
                </div>
            );
        } else {
            return null;
        }
    }

    sceneMenu = () => (
        <Menu
            id="simple-menu"
            anchorEl={this.state.anchorEl}
            open={Boolean(this.state.anchorEl)}
            onClose={this.handleClose} >
            <MenuItem
                onClick={() => { this.handleClose(); this.handleQrToggle(); }}>
                <ListItemIcon >
                    <Icon className="material-icons">gradient</Icon>
                </ListItemIcon>
                <ListItemText inset primary="QR Code" />
            </MenuItem>
            <MenuItem
                onClick={() => { this.handleClose(); this.handleShrToggle(); }}>
                <ListItemIcon >
                    <Icon className="material-icons">send</Icon>
                </ListItemIcon>
                <ListItemText inset primary="Send" />
            </MenuItem>
            {/* <MenuItem
        onClick={() => { this.handleClose(); this.handlePwToggle(); }}>
        <ListItemIcon >
          <Icon className="material-icons">lock</Icon>
        </ListItemIcon>
        <ListItemText inset primary="PW Protect" />
      </MenuItem> */}
        </Menu>
    );

    handleChange = (event, value) => {
        this.setState({ value });
    };

    render() {
        const { classes } = this.props;
        let previewToggle = {
            //paddingtop: 10,
            position: "fixed",
            top: 10,
            right: "46%",
        };
        const style = {
            default: {
                margin: 2,
                padding: 0,
                color: "#fff",
            }
        };
        const userProjs = [].concat(this.props.userProjs);
        const exampleProjs = [].concat(this.props.exampleProjs);
        return (
            <div>
                <React.Fragment>
                    {
                        !this.props.hideTooltip ?
                            <Tooltip title="Open" placement="bottom-start">
                                <IconButton
                                    id="open-btn"
                                    onClick={this.props.handleProjectToggle}
                                    className="header-btn"
                                    aria-haspopup="true"
                                    style={style.default}>
                                    <Icon className="material-icons">perm_media</Icon>
                                </IconButton>
                            </Tooltip>
                            : null
                    }
                    <Modal
                        aria-labelledby="simple-modal-title"
                        aria-describedby="simple-modal-description"
                        open={this.props.projectsOpen}
                        onClose={this.props.handleProjectToggle}>
                        <div style={getOuterModalStyle()} className={classes.outer}>
                            <IconButton
                                color="default"
                                style={exitBtnStyle}
                                onClick={this.props.handleProjectToggle}>
                                <Icon className="material-icons">close</Icon>
                            </IconButton>
                            <div>
                                <Button
                                    style={previewToggle}
                                    onClick={() => this.setState({ showImg: !this.state.showImg })}>
                                    { // If we are showing the img, show the proper icon
                                        this.state.showImg
                                            ?
                                            <Icon className="material-icons">visibility_off</Icon>
                                            :
                                            <Icon className="material-icons">visibility</Icon>
                                    }
                                    <span>&nbsp;</span>Preview
                                </Button>
                                <hr />
                            </div>
                            <Tabs
                                value={this.state.value}
                                onChange={this.handleChange} >
                                <Tab
                                    label="Your Projects"
                                    value="a" />
                                <Tab
                                    label="Example Scenes"
                                    value="b" />
                            </Tabs>
                            {this.state.value === "a" &&
                                <div id="project-list" style={{ marginTop: 0, overflow: "scroll" }}>
                                    <div className="row" id="user-proj" style={{ width: "100%" }}>
                                        { // Sort the users projects in alphabetical order
                                            userProjs.sort(function (a, b) {
                                                return a.name < b.name ? -1 : a.name > b.name ? 1 : a.updateTime > b.updateTime ? -1 : a.updateTime < b.updateTime ? 1 : 0;
                                            }).map(proj => {
                                                return this.helper(proj, true);
                                            })
                                        }
                                    </div>
                                </div>}
                            {this.state.value === "b" &&
                                <div id="project-list" style={{ marginTop: 0, overflow: "scroll" }}>
                                    <div className="row" id="sample-proj" style={{ width: "100%" }}>
                                        {
                                            exampleProjs.sort(function (a, b) {
                                                return a.name < b.name ? -1 : a.name > b.name ? 1 : a.updateTime > b.updateTime ? -1 : a.updateTime < b.updateTime ? 1 : 0;
                                            }).map(proj => {
                                                return this.helper(proj, false);
                                            })
                                        }
                                    </div>
                                </div>}
                            <this.sceneMenu />

                            <Modal
                                aria-labelledby="simple-modal-title"
                                aria-describedby="simple-modal-description"
                                open={this.state.infoOpen}
                                onClose={this.handleInfoToggle} >
                                <div style={getInfoModalStyle()} className={classes.info}>
                                    <ButtonBase
                                        style={{ position: "absolute", right: 15, top: 15 }}
                                        onClick={() => this.handleInfoToggle()} >
                                        <Icon className="material-icons">clear</Icon>
                                    </ButtonBase >
                                    <this.infoOpen />
                                </div>
                            </Modal>
                            <Modal
                                aria-labelledby="simple-modal-title"
                                aria-describedby="simple-modal-description"
                                open={this.state.qrCodeOpen}
                                onClose={this.handleQrToggle} >
                                <div style={getModalStyle()} className={classes.paper}>
                                    <ButtonBase
                                        style={{ position: "absolute", right: 15, top: 15 }}
                                        onClick={() => this.handleQrToggle()} >
                                        <Icon className="material-icons">clear</Icon>
                                    </ButtonBase >
                                    <this.qrCodeOpen />
                                </div>
                            </Modal>
                            <Modal
                                aria-labelledby="simple-modal-title"
                                aria-describedby="simple-modal-description"
                                open={this.state.shareOpen}
                                onClose={this.handleShrToggle} >
                                <div style={getModalStyle()} className={classes.paper}>
                                    <ButtonBase
                                        style={{ position: "absolute", right: 15, top: 15 }}
                                        onClick={() => this.handleShrToggle()} >
                                        <Icon className="material-icons">clear</Icon>
                                    </ButtonBase >
                                    <this.shareOptions />
                                </div>
                            </Modal>
                            <Modal
                                aria-labelledby="simple-modal-title"
                                aria-describedby="simple-modal-description"
                                open={this.state.pwProtectOpen}
                                onClose={this.handlePwToggle} >
                                <div style={getModalStyle()} className={classes.paper}>
                                    <ButtonBase
                                        style={{ position: "absolute", right: 15, top: 15 }}
                                        onClick={() => this.handlePwToggle()} >
                                        <Icon className="material-icons">clear</Icon>
                                    </ButtonBase >
                                    <this.pwProtect />
                                </div>
                            </Modal>
                        </div>
                    </Modal>
                </React.Fragment>
            </div>
        );
    }
}

const ProjectView = withStyles(modelStyles)(Project);

export default ProjectView;

