     */
    setColor = (color = "white") => {
        this.cursor.color = color.toLowerCase();
        return this.cursor.color;
    }

    setCursorAttribute = (key = "", value = "") => {
        if (typeof (key) !== "string" || key === "") {
            console.error("Error: Invalid key");
            return this.cursor;
        }
        switch (key.toLowerCase()) {
            case "color":
                this.setColor(value);
